jQuery(function($){
	$("#chara_detailA img").bind("load",function(){
		var ImgHeight = $(this).height();
		$('.chara_detailA').css('height',ImgHeight);
	});
	
	$('#thumbA a').click(function(){
		if($(this).hasClass('active') == false){
			$('#thumbA a').removeClass('active');
			$(this).addClass('active');
			$('.chara_detailA img').hide().attr('src',$(this).attr('href')).fadeIn();
		};
		return false;
	}).filter(':eq(0)').click();
});

